mapper.select:
select *
from smanagelist
where cdel = 0 and cselect= #{cselect}
order by mid desc;

mapper.update:
update smanagelist
set 
ssub = #{ssub} , sid=#{sid}, sname=#{sname} , sphone=#{sphone}, seamil=#{semail}
where smid = #{smid}

mapper.delete:
delete
from smanagelist
where cdel = 1 and smid = #{smid}
